import './App.css';
import Navbar from './navbar.jsx';
import GameBoard from './gameBoard.jsx';
import Square from './square.jsx';
import React, { useState } from "react";
const newBoard = [
  ['X', null, null],
  [null, "O", null],
  [null, null, null],
]
function App() {
  let [game, setGame] = useState({
    currentPlayer: 'O',
    oScore: 0,
    xScore: 0,
    board: newBoard
  });

  const nextTurn = () => {
    const nextPlayer = game.currentPlayer === 'O' ? 'X' : 'O'
    return nextPlayer
  };

  const takeTurn = (event, i) => {
    event.preventDefault();
    let currentBoard = game.board

    setGame ({
      ...game,
      board:currentBoard,
      currentPlayer: nextTurn(),
    });
  }
  return (
    <div className="App">
      <Navbar currentPlayer = {game.currentPlayer} onClick={nextTurn} />
      <GameBoard>
      {
          game.board.flat().map((square, i) => {
            return (
              <Square key={i} value={square} onClick={(event) => takeTurn(event, i)}/>
            );
          })
        };
      </GameBoard>
    </div>
  )
}

export default App;
