import './App.css';
import Navbar from './navbar.jsx';
import React, { useState } from "react";
function App() {
  let [game, setGame] = useState({
    currentPlayer: 'O',
    oScore: 0,
    xScore: 0
  });

  const nextTurn = () => {
    const nextPlayer = game.currentPlayer === 'O' ? 'X' : 'O'
    setGame({
      ...game,
      currentPlayer: nextPlayer,
    });
  };

  return (
    <div className="App">
      <Navbar currentPlayer = {game.currentPlayer} onClick={nextTurn} />
    </div>
  );
}

export default App;
