import React from "react";
import './ScoreBoard.css'
import oMarker from '../images/icon-o.svg';
import xMarker from '../images/icon-x.svg';

export const ScoreBoard = ( scores, xPlaying ) => {
  const { xScore, oScore } = scores;
  return (
    <div className="scoreborad">
      <img className={`score x-score ${!xPlaying && "inactive"}`} src={xMarker} alt="x"/>  - {xScore}
      <img className={`score x-score ${xPlaying && "inactive"}`} src={oMarker}  alt="o"/> - {oScore}
    </div>
  )
}
