  [null, null, null],
  [null, null, null],
  [null, null, null],
]
function App() {
  let [game, setGame] = useState({
    currentPlayer: 'O',
    oScore: 0,
    xScore: 0,
    board: newBoard
  });

  const nextTurn = () => {
    const nextPlayer = game.currentPlayer === 'O' ? 'X' : 'O'
    setGame({
      ...game,
      currentPlayer: nextPlayer,
    });
  };

  return (
    <div className="App">
      <Navbar currentPlayer = {game.currentPlayer} onClick={nextTurn} />
      <GameBoard />
    </div>
  );
}

export default App;
