import './App.css';
import Navbar from './Components/Navbar';
import GameBoard from './Components/GameBoard';
import Square from './Components/Square';
import React, { useState, useEffect } from "react";

const newBoard = [
  [null, null, null],
  [null, null, null],
  [null, null, null],
]

function App() {
  let [game, setGame] = useState({
    currentPlayer: 'X',
    oScore: 0,
    xScore: 0,
    board: newBoard,
    gamever: false
  });

  const nextTurn = () => {
    const nextPlayer = game.currentPlayer === 'X' ? 'O' : 'X'
    return nextPlayer
  };

  const checkWin = (currentBoard) => {
    // Rows
    for (let row = 0; row < 3; row++) {
      if (
        currentBoard[row][0] === currentBoard[row][1] &&
        currentBoard[row][1] === currentBoard[row][2] &&
        currentBoard[row][0] !== null
      ) {
        return true
      }
    }

    // Columns
    for (let col = 0; col < 3; col++) {
      if (
        currentBoard[0][col] === currentBoard[1][col] &&
        currentBoard[1][col] === currentBoard[2][col] &&
        currentBoard[0][col] !== null
      ) {
        return true
      }
    }

    //Diagonals
    if(
      (currentBoard[0][0] === currentBoard[1][1] &&
        currentBoard[1][1] === currentBoard[2][2] &&
        currentBoard[0][0] !==null ) ||
      (currentBoard[0][2] === currentBoard[1][1] &&
        currentBoard[1][1] === currentBoard[2][0] &&
        currentBoard[0][2] !== null)
    ) {
      return true
    }
    return false
  }

  const resetBoard = () => {
    setGame({
      ...game,
      board: newBoard,
      currentPlayer: 'O',
      gameover: false
    })
  }

  const takeTurn = (event, i) => {
    event.preventDefault();
      // Don't continue if game is already over
    if(game.gameOver) return;
    let currentBoard = game.board;
    i === 0 ? currentBoard[0][0] = game.currentPlayer
      : i === 1 ? currentBoard[0][1] = game.currentPlayer
      : i === 2 ? currentBoard[0][2] = game.currentPlayer
      : i === 3 ? currentBoard[1][0] = game.currentPlayer
      : i === 4 ? currentBoard[1][1] = game.currentPlayer
      : i === 5 ? currentBoard[1][2] = game.currentPlayer
      : i === 6 ? currentBoard[2][0] = game.currentPlayer
      : i === 7 ? currentBoard[2][1] = game.currentPlayer
      : currentBoard[2][2] = game.currentPlayer

    setGame ({
      ...game,
      board:currentBoard,
      currentPlayer: nextTurn(),
    });

    if (checkWin(currentBoard)) {
      // Playes wins, update game state to top the game

      const currentPlayer = game.currentPlayer;
      const updateScore = game[currentPlayer.toLowerCase() + 'Score'] + 1;

      if(updateScore === 5) {
        // Stop the game and reset scores
        setGame({
          ...game,
          board: currentBoard,
          gameover:true,
          xScore: 0,
          oScore: 0
        })
        console.log(`player ${game.currentPlayer} wins!`);
        return;
      } else {
        setGame({
          ...game,
          board: currentBoard,
          currentPlayer: nextTurn(),
          [currentPlayer.toLocaleLowerCase() + 'Socre']: updateScore
        });
        console.log(`Player ${game.currentPlayer} wins this round!`);
        resetBoard();
    }
    return;
  }

  // Check for a draw
  const isDraw = currentBoard.flat().every((cell) => cell !== null);
  if (isDraw) {
    // Draw, update game state to stop the game
    setGame({
      ...game,
      board: currentBoard,
      currentPlayer: game.currentPlayer,
      gameOver: true,
    });
    console.log('It\'s a draw!');
    return;
  }

  // Switch to the next player's turn
  setGame({
    ...game,
    board: currentBoard,
    currentPlayer: nextTurn(),
  });
};

useEffect(() => {
  // Enable the next button when the game is over
  const nextButton = document.getElementById('next-button');
  if (nextButton) {
    nextButton.disabled = !game.gameOver;
  }
}, [game.gameOver]);


return (
  <div className="App">
    <Navbar currentPlayer={game.currentPlayer} onClick={nextTurn} />
    <GameBoard>
      {game.board.flat().map((square, i) => {
        return <Square key={i} value={square} onClick={(event) => takeTurn(event, i)} />;
      })}
    </GameBoard>
    <button id="next-button" onClick={resetBoard} disabled={!game.gameOver}>
      Next Game
    </button>
  </div>
);
}

export default App;
